export default function CreateProduct() {
  const { inputs, handleChange } = useForm({
    name: 'Nice shoes',
    price: 34234,
    description: 'These are the best shoes'
  });
  return (
    <form>
      <label htmlFor="name">
        Name
        <input
          type="text"
          id="name"
          name="name"
          placeholder="Name"
          value={inputs.name}
          onChange={handleChange}
        />
      </label>
